mapper.select:

SELECT l.`id`, l.`staff_id`, l.`company_id`, l.`conglomerate_id`, l.`content`, l.`uptime`, l.`uptimeC`,s.`staff_name` as `staff_name`,z.`company_name` as `company_name` FROM `attendance`.`log` l
LEFT JOIN `attendance`.`staff` s ON l.`staff_id`=s.`id`
LEFT JOIN `attendance`.`company` z ON l.`company_id`=z.`id`
WHERE l.`conglomerate_id`=#{conglomerate_id}
limit #{page},#{pageSize}


SELECT l.`id`, l.`staff_id`, l.`company_id`, l.`conglomerate_id`, l.`content`, l.`uptime`, l.`uptimeC`,s.`staff_name` as `staff_name`,z.`company_name` as `company_name` FROM `attendance`.`log` l
LEFT JOIN `attendance`.`staff` s ON l.`staff_id`=s.`id`
LEFT JOIN `attendance`.`company` z ON l.`company_id`=z.`id`
WHERE l.`staff_id`=#{staff_id}
ORDER BY l.`uptime` DESC
limit #{page},#{pageSize}
