mapper.select:

SELECT p.`id`, p.`conglomerate_id`, p.`items_id`, p.`staff_id`, p.`participant`, p.`project_name`, p.`start_time`, p.`start_timeC`, p.`end_time`, p.`end_timeC`, p.`amount`, p.`uptime`, p.`uptimeC`, i.`items_name` AS `items_name`, s.`staff_name` AS `staff_name` FROM `attendance`.`project` p
LEFT JOIN `attendance`.`items` i ON p.`items_id`=i.`id`
LEFT JOIN `attendance`.`staff` s ON p.`staff_id`=s.`id`

        p.`conglomerate_id`=#{conglomerate_id}

        and i.`items_name` like concat("%",#{items_name},"%")


        and p.`project_name` like concat("%",#{project_name},"%")


        and  p.`uptime` >= #{start} and p.`uptime` <= #{end}


ORDER BY p.`uptime` DESC
limit #{page},#{pageSize}

SELECT COUNT(p.id) FROM `attendance`.`project` p
LEFT JOIN `attendance`.`items` i ON p.`items_id`=i.`id`

    p.`conglomerate_id`=#{conglomerate_id}

        and i.`items_name` like concat("%",#{items_name},"%")


        and p.`project_name` like concat("%",#{project_name},"%")


        and  p.`uptime` >= #{start} and p.`uptime` <= #{end}

SELECT p.`id`, p.`conglomerate_id`, p.`items_id`, p.`staff_id`, p.`participant`, p.`project_name`, p.`start_time`, p.`start_timeC`, p.`end_time`, p.`end_timeC`, p.`amount`, p.`uptime`, p.`uptimeC`, i.`items_name` AS `items_name`, s.`staff_name` AS `staff_name` FROM `attendance`.`project` p LEFT JOIN `attendance`.`items` i ON p.`items_id`=i.`id` LEFT JOIN `attendance`.`staff` s ON p.`staff_id`=s.`id`
p.`staff_id`=#{staff_id}
and i.`items_name` like concat("%",#{items_name},"%")
and p.`project_name` like concat("%",#{project_name},"%")
ORDER BY p.`uptime` DESC limit #{page},#{pageSize}

SELECT p.`id`,p.`project_name` FROM `attendance`.`project` p
WHERE p.`staff_id`=#{staff_id}
ORDER BY p.`uptime` DESC

SELECT p.`id`,p.`project_name` FROM `attendance`.`project` p
WHERE p.`conglomerate_id`=#{conglomerate_id}
ORDER BY p.`uptime` DESC
