mapper.select:

SELECT s.`id`, s.`staff_name`, s.`staff_age`, s.`staff_img`, s.`staff_sex`, s.`staff_card`, s.`staff_nation`, s.`staff_address`, s.`staff_phone`, s.`sos_name`, s.`sos_ship`, s.`sos_phone`, s.`entry_time`, s.`company_id`, s.`department_id`, s.`position_id`, s.`password`, s.`picture`, s.`nickname`, s.`signature`, s.`state`, s.`clockstatus`, l.`company_name` as `company_name`, d.`department_name` as `department_name`, p.`position_name` as `position_name` FROM `attendance`.`staff` s
LEFT JOIN `attendance`.`company` l ON s.`company_id`=l.`id`
LEFT JOIN `attendance`.`department` d ON s.`department_id`=d.`id`
LEFT JOIN `attendance`.`position` p ON s.`position_id`=p.`id`

    s.`company_id`=#{company_id}

        and s.`staff_name` like concat("%",#{staff_name},"%")


        and s.`department_id`=#{department_id}


        and s.`position_id`=#{position_id}


order by s.`entry_time` desc
limit #{page},#{pageSize}

SELECT COUNT(id) FROM `attendance`.`staff`

    `company_id`=#{company_id}

        and `staff_name` like concat("%",#{staff_name},"%")


        and `department_id`=#{department_id}


        and `position_id`=#{position_id}
